sprites.onCreated(SpriteKind.Enemy, function (sprite) {
    if (sprite.image == list[4]) {
        sprites.setDataNumber(sprite, "CanColour", randint(0, 6))
        sprites.setDataImageValue(sprite, "Img", assets.image`TomatoSoup`)
        if (sprites.readDataNumber(sprite, "CanColour") == 0) {
            sprites.readDataImage(sprite, "Img").replace(6, 5)
            sprites.readDataImage(sprite, "Img").replace(7, 8)
        } else if (sprites.readDataNumber(sprite, "CanColour") == 1) {
            sprites.readDataImage(sprite, "Img").replace(6, 4)
            sprites.readDataImage(sprite, "Img").replace(7, 3)
        } else if (sprites.readDataNumber(sprite, "CanColour") == 2) {
            sprites.readDataImage(sprite, "Img").replace(6, 2)
            sprites.readDataImage(sprite, "Img").replace(7, 1)
        } else if (sprites.readDataNumber(sprite, "CanColour") == 3) {
            sprites.readDataImage(sprite, "Img").replace(6, 14)
            sprites.readDataImage(sprite, "Img").replace(7, 15)
        } else if (sprites.readDataNumber(sprite, "CanColour") == 4) {
            sprites.readDataImage(sprite, "Img").replace(6, 11)
            sprites.readDataImage(sprite, "Img").replace(7, 3)
        } else if (sprites.readDataNumber(sprite, "CanColour") == 5) {
            sprites.readDataImage(sprite, "Img").replace(6, 13)
            sprites.readDataImage(sprite, "Img").replace(7, 6)
        } else if (sprites.readDataNumber(sprite, "CanColour") == 6) {
            sprites.readDataImage(sprite, "Img").replace(6, 8)
        }
        sprite.setImage(sprites.readDataImage(sprite, "Img"))
    }
    sprites.setDataNumber(sprite, "Position", randint(0, 3))
    if (sprites.readDataNumber(sprite, "Position") == 0) {
        sprite.setPosition(randint(SpritePlayer.x - 110, SpritePlayer.x + 110), randint(SpritePlayer.y - 80, SpritePlayer.y - 90))
    } else if (sprites.readDataNumber(sprite, "Position") == 1) {
        sprite.setPosition(randint(SpritePlayer.x - 110, SpritePlayer.x + 110), randint(SpritePlayer.y + 80, SpritePlayer.y + 90))
    } else if (sprites.readDataNumber(sprite, "Position") == 2) {
        sprite.setPosition(randint(SpritePlayer.x - 110, SpritePlayer.x - 120), randint(SpritePlayer.y - 90, SpritePlayer.y + 90))
    } else if (sprites.readDataNumber(sprite, "Position") == 3) {
        sprite.setPosition(randint(SpritePlayer.x + 110, SpritePlayer.x + 120), randint(SpritePlayer.y - 90, SpritePlayer.y + 90))
    }
    sprite.follow(SpritePlayer, 25)
})
